class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n,m = len(grid),len(grid[0])
        ans = 0
        for i in range(n // 2):
            for j in range(m// 2):
                c = grid[i][j] + grid[n - i - 1][j] + grid[i][m - j - 1] + grid[n - i - 1][m - j - 1]
                ans += min(4 - c,c)
        c = 0
        if n&1:
            for j in range(m // 2):
                if grid[n // 2][j] == grid[n // 2][m - j - 1] and grid[n // 2][m - j - 1] == 1:
                    c += 2
        if m&1:
            for i in range(n // 2):
                if grid[i][m // 2] == grid[n - i - 1][m // 2] and grid[n - i - 1][m // 2] == 1:
                    c += 2
        if n&1:
            for j in range(m // 2):
                if grid[n // 2][j] != grid[n // 2][m - j - 1]:
                    ans += 1
                    if c % 4 != 0:
                        c = 0
        if m&1:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - i - 1][m // 2]:
                    ans += 1
                    if c % 4 != 0:
                        c = 0
        if n&1 and m&1 and grid[n // 2][m // 2] == 1:
            ans += 1
        return ans + (2 if c % 4 != 0 else 0)
