class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int res=0;
        int n2=n/2,m2=m/2;
        int t0,t1;
        for(int i=0;i<n2;++i)
        {
            for(int j=0;j<m2;++j)
            {
                t1=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                t0=4-t1;
                res+=min(t0,t1);
            }
        }
        t0=t1=0;
        if(n%2){
            for(int j=0;j<m2;++j)
               if(grid[n2][j]!=grid[n2][m-j-1]){
                    ++res;
                    ++t1;
               }else if(grid[n2][j]){
                    t0+=2;
               }
        }
        if(m%2){
            for(int i=0;i<n2;++i)
               if(grid[i][m2]!=grid[n-i-1][m2]){
                    ++res;
                    ++t1;
               }else if(grid[i][m2]){
                    t0+=2;
               }
        }
        if(t0%4&&!t1){
            res+=2;
        }
        if((m%2)&&(n%2)&&grid[n2][m2]){
            ++res;
        }
        return res;
    }
};
