class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int res = 0;
        for (int i = 0; i < (n >> 1); i++) {
            for (int j = 0; j < (m >> 1); j++) {
                int tmp = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] +
                          grid[n - 1 - i][m - 1 - j];
                res += min(tmp, 4 - tmp);
                // cout << i << " " << j << " " << tmp << endl;
            }
        }
        if ((n & 1) && (m & 1)) {
            res += grid[n >> 1][m >> 1];
        }
        int cnt[3] = {0};
        if (n & 1) {
            for (int j = 0; j < (m >> 1); j++) {
                int tmp = grid[n >> 1][j] + grid[n >> 1][m - 1 - j];
                cnt[tmp]++;
            }
        }
        if (m & 1) {
            for (int i = 0; i < (n >> 1); i++) {
                int tmp = grid[i][m >> 1] + grid[n - 1 - i][m >> 1];
                cnt[tmp]++;
            }
        }
        cout<<res<<" "<<cnt[1]<<" "<<cnt[2]<<endl;
        if (cnt[2] % 2 == 0) {
            res += cnt[1];
        } else {
          if(cnt[1]>=1){
            res+=cnt[1];
          }else{
            res+=2+cnt[1];
          }
        }
        return res;
    }
};
