class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n = (int) v.size();
        int m = (int) v[0].size();

        int ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if ((n % 2 && i == n / 2) || (m % 2 && j == m / 2)) {
                    continue;
                }
                int cnt = v[i][j] + v[n - 1 - i][j] + v[i][m - 1 - j] + v[n - 1 - i][m - 1 - j];
                ans += min(cnt, 4 - cnt);
            }
        }
        ans /= 4;

        if ((n % 2) && (m % 2) && v[n / 2][m / 2]) {
            ans++;
            v[n / 2][m / 2] = 0;
        }

        int cnt = 0;
        int half = 0;
        if (n & 1) {
            for (int j = 0; j < m / 2; j++) {
                int cur = v[n / 2][j] ^ v[n / 2][m - 1 - j];
                half += cur;
                cnt += 2 * (cur || (!cur && v[n / 2][j]));
                ans += cur;
            }
        }

        if (m & 1) {
            for (int i = 0; i < n / 2; i++) {
                int cur = v[i][m / 2] ^ v[n - 1 - i][m / 2];
                half += cur;
                cnt += 2 * (cur || (!cur && v[i][m / 2]));
                ans += cur;
            }
        }

        if (cnt % 4 == 2 && !half) {
            ans += 2;
        }

        return ans;
    }
};
