class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int ans=0;
        int n=a.size(),m=a[0].size();
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int c = a[i][j]+a[n-1-i][j]+a[i][m-1-j]+a[n-1-i][m-1-j];
                if(4-c<c)c=4-c;
                ans+=c;
                a[i][j]=2;
                a[n-1-i][j]=2;
                a[i][m-1-j]=2;
                a[n-1-i][m-1-j]=2;
            }
        }
        int lol=0,hmm=0;
        if(n%2){
            for(int i=0;i<m;i++)
            {
                if(i>=m-1-i)break;
                if(a[n/2][i]==a[n/2][m-1-i]){
                    if(a[n/2][i]==1)lol+=2;
                }
                else {ans++;hmm=1;}
            }
        }
        if(m%2){
            for(int i=0;i<n;i++)
            {
                if(i>=n-1-i)break;
                if(a[i][m/2]==a[n-1-i][m/2]){
                    if(a[i][m/2]==1)lol+=2;
                }
                else {ans++;hmm=1;}
            }
        }
        if(n%2 && m%2){
            if(a[n/2][m/2])ans++;
        }
        if(lol%4){
            if(hmm==0)ans+=2;
        }
        return ans;
    }
};
