class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int ans = 0, n = v.size(), m = v[0].size();
        for(int i = 0; i < n / 2;i++){
            for(int j = 0;  j < m / 2; j++){
                int cnt = v[i][j];
                cnt += v[n - i - 1][j];

                cnt += v[i][m - j - 1];

                cnt += v[n - i - 1][m - j - 1]; 
                ans += min(cnt, 4 - cnt);
            }
        }
        int c = 0, a = 0,x = 0;
        if(n & 1){
            a++;
            for(int i = 0; i <m / 2; i++){
                if(v[n / 2][i] == v[n / 2][m - i - 1]){
                    if(v[n / 2][i])c+=2;
                }else x++,c+=2;
            }
        }
        if(m & 1){
            a++;
            for(int i = 0; i < n / 2; i++){
               // cout << i << " " << m / 2 <<" "<<v[i][m / 2]<< endl;
                if(v[i][m / 2] == v[n - i - 1][m / 2]){
                    if(v[i][m/2])c+=2;
                }else x++,c+=2;
            }
        }
        cout << n << " " << m << ' ' << x << " " << c << " " << ans << endl;
        if(a == 2)ans+=v[n / 2][m / 2];
        c %= 4;
        ans += x;
        if(c && !x)ans+=2;
        return ans;
    }
};
