class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size();
        int m = grid[0].size();
        if(n == m && n == 1){
            return grid[0][0];
        }
        int rec = 0;
        int rec2 = 0;
        vector<vector<int>> vis(n, vector<int>(m, 0));
        for(int i = 0;i < n;i ++){
            for(int j = 0;j < m;j ++){
                int cnt1 = 0;
                 if(vis[i][j])
                    continue;
                int t = 0;
                int ii = n - i - 1;
                int jj = m - j - 1;
                if(vis[ii][j] == 0){
                    cnt1 += grid[ii][j];
                    vis[ii][j] = 1;
                    t += 1;
                } 
                if(vis[i][j] == 0){
                    cnt1 += grid[i][j];
                    vis[i][j] = 1;
                    t += 1;
                } 
                if(vis[i][jj] == 0){
                    cnt1 += grid[i][jj];
                    vis[i][jj] = 1;
                    t += 1;
                } 
                if(vis[ii][jj] == 0){
                    cnt1 += grid[ii][jj];
                    vis[ii][jj] = 1;
                    t += 1;
                } 
                if(t == 2 ){
                    if(cnt1 == 2)
                        rec += 1;
                    else if(cnt1 == 1)
                        rec2 += 1;
                }
                if(t == 1 && cnt1 == 1)
                    ans += 1;
                ans += min(cnt1, t - cnt1);

            }
        }

        if(rec % 2 && rec2 == 0)
            ans += 2;
       return ans;
    }
};
