class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        ll N = grid.size();
        ll M = grid[0].size();


        // for (ll i = 0;i<N;i++) {
        //     for (ll j = 0;j<N;j++) {
        //         S += grid[i][j];
        //     }
        // }

        ll S = 0;

        for (ll i = 0;i<N/2;i++) {
            for (ll j = 0;j<M/2;j++) {
               ll a =0;
               a += (grid[i][j]==0?-1:1);
               a += (grid[N-i-1][j]==0?-1:1);
               a += (grid[i][M-j-1]==0?-1:1);
               a += (grid[N-i-1][M-j-1]==0?-1:1);
               S += min((4+a)/2, (4-a)/2);
            }
        }

        // 나머지가 중요
        ll SS = 0;
        ll SM = 0;

        if (N%2 == 1 && M%2 == 1) {
            // 가온데는? 0이어야함
            if (grid[N/2][M/2] == 1) S++;
            for (ll i = 0;i<N/2;i++) {
                if (grid[i][M/2] != grid[N-i-1][M/2]) {
                    SS += 1;
                }else if (grid[i][M/2] == 1) {
                    SM ++;
                }
            }
            for (ll j = 0;j<M/2;j++) {
                if (grid[N/2][j] != grid[N/2][M-j-1]) {
                    SS += 1;
                }else if (grid[N/2][j] == 1) {
                    SM ++;
                }
            }
        }else if (N%2 == 1) {
            for (ll j = 0;j<M/2;j++) {
                if (grid[N/2][j] != grid[N/2][M-j-1]) {
                    SS += 1;
                }else if (grid[N/2][j] == 1) {
                    SM ++;
                }
            }
        }else if (M%2 == 1) {
            for (ll i = 0;i<N/2;i++) {
                if (grid[i][M/2] != grid[N-i-1][M/2]) {
                    SS += 1;
                }else if (grid[i][M/2] == 1) {
                    SM ++;
                }
            }
        }
        S += SS;
        if (SM%2 == 1) {
            if (SS == 0) {
                S += 2;
            }
        }
        return S;



    }
};
