class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int row = grid.size();
        int col = grid[0].size();

        int res = 0;

        for (int i = 0; i < row / 2; i++) {
            for (int j = 0; j < col / 2; j++) {
                int one = 0;

                if (grid[i][j]) one++;
                if (grid[row - 1 - i][j]) one++;
                if (grid[i][col - 1 - j]) one++;
                if (grid[row - 1 - i][col - 1 - j]) one++;
                // cout << one << "\n";
                res += min(one, 4 - one);
            }
        }

        // cout << res << "\n";

        int extrarow = 0;
        int rowone = 0;
        int extracol = 0;
        int colone = 0;

        if (row % 2) {
            for (int i = 0; i < col / 2; i++) {
                if (grid[row / 2][i] != grid[row / 2][col - 1 - i]) extrarow++;
                if (grid[row / 2][i]) rowone++;
                if (grid[row / 2][col - 1 - i]) rowone++;
            }
        }
        if (col % 2) {
            for (int i = 0; i < row / 2; i++) {
                if (grid[i][col / 2] != grid[row - 1 - i][col / 2]) extracol++;
                if (grid[i][col / 2]) colone++;
                if (grid[row - 1 - i][col / 2]) colone++;
            }
        }

        if ((extrarow + extracol) == 0 && (rowone + colone) % 4 == 2) {
            res += 2;
        }

        if (row % 2 && col % 2 && grid[row / 2][col / 2]) {
            return res + extrarow + extracol + 1;
        } else {
            return res + extrarow + extracol;
        }

    }
};
