class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int i,j,m=v.size(),n=v[0].size(),ans=0;
        for(i=0;i<m/2;i++){
            for(j=0;j<n/2;j++){
                int c=0;
                c+=v[i][j];
                c+=v[m-i-1][j];
                c+=v[m-i-1][n-j-1];
                c+=v[i][n-j-1];
                if(c>2){
                    ans+=4-c;
                }
                else{
                    ans+=c;
                }
            }
        }
        if(n%2==1&&m%2==1){
            if(v[m/2][n/2]==1){
                ans++;
            }
        }

        int a1=0,a=0;
        if(n%2==1){
            int x=n/2;
            for(i=0;i<m/2;i++){
                if(v[i][x]==v[m-i-1][x]){
                    if(v[i][x]==1){
                        a1++;
                    }
                }
                else{
                    a++;
                }
            }
        }
        if(m%2==1){
            int x=m/2;
            for(i=0;i<n/2;i++){
                if(v[x][i]==v[x][n-i-1]){
                    if(v[x][i]==1){
                        a1++;
                    }
                }
                else{
                    a++;
                }
            }

        }
        if(a==0){
            if(a1%2!=0){
                ans+=2;
            }
        }
        else{
            ans+=a;
        }
        return ans;
    }
};
