class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        res = 0
        one = 0
        change = 0
        if m % 2 and n % 2:
            res += grid[m//2][n//2]
            for j in range(n//2):
                one += grid[m//2][j] + grid[m//2][-(j+1)]
                if grid[m//2][j] != grid[m//2][-(j+1)]:
                    change += 1
                    one -= 1
            for i in range(m //2):
                one += grid[i][n//2] + grid[-(i+1)][n//2]
                if grid[i][n//2] != grid[-(i+1)][n//2]:
                    change += 1
                    one -= 1
            if not change and one % 4:
                res += change + 2
            else:
                res += change
        elif m % 2:
            for j in range(n//2):
                one += grid[m//2][j] + grid[m//2][-(j+1)]
                if grid[m//2][j] != grid[m//2][-(j+1)]:
                    change += 1
                    one -= 1
            if not change and one % 4:
                res += change + 2
            else:
                res += change
        elif n % 2:
            for i in range(m //2):
                one += grid[i][n//2] + grid[-(i+1)][n//2]
                if grid[i][n//2] != grid[-(i+1)][n//2]:
                    change += 1
                    one -= 1
            if not change and one % 4:
                res += change + 2
            else:
                res += change
        m //= 2
        n //= 2
        for i in range(m):
            for j in range(n):
                cnt = grid[i][j] + grid[-(i+1)][j] + grid[i][-(j+1)] + grid[-(i+1)][-(j+1)]
                res += min(cnt, 4-cnt)

        return res
