class Solution {
    public int minFlips(int[][] grid) {
        int flips = 0;
        int m = grid.length, n = grid[0].length;
        int maxRow = m / 2 - 1, maxCol = n / 2 - 1;
        for (int i = 0; i <= maxRow; i++) {
            for (int j = 0; j <= maxCol; j++) {
                int num1 = grid[i][j];
                int num2 = grid[i][n - 1 - j];
                int num3 = grid[m - 1 - i][j];
                int num4 = grid[m - 1 - i][n - 1 - j];
                int sum = num1 + num2 + num3 + num4;
                flips += Math.min(sum, 4 - sum);
            }
        }
        if (m % 2 != 0 && n % 2 != 0) {
            int midRow = m / 2, midCol = n / 2;
            if (grid[midRow][midCol] == 1) {
                grid[midRow][midCol] = 0;
                flips++;
            }
            int sum = 0;
            for (int i = 0; i < m; i++) {
                sum += grid[i][midCol];
            }
            for (int j = 0; j < n; j++) {
                sum += grid[midRow][j];
            }
            int rowFlips = 0;
            for (int i = 0, j = n - 1; i < j; i++, j--) {
                if (grid[midRow][i] != grid[midRow][j]) {
                    rowFlips++;
                }
            }
            int colFlips = 0;
            for (int i = 0, j = m - 1; i < j; i++, j--) {
                if (grid[i][midCol] != grid[j][midCol]) {
                    colFlips++;
                }
            }
            flips += rowFlips + colFlips;
            if (sum % 4 != 0 && rowFlips == 0 && colFlips == 0) {
                flips += 2;
            }
        } else if (m % 2 != 0) {
            int midRow = m / 2;
            int sum = 0;
            for (int j = 0; j < n; j++) {
                sum += grid[midRow][j];
            }
            int rowFlips = 0;
            for (int i = 0, j = n - 1; i < j; i++, j--) {
                if (grid[midRow][i] != grid[midRow][j]) {
                    rowFlips++;
                }
            }
            flips += rowFlips;
            if (sum % 4 != 0 && rowFlips == 0) {
                flips += 2;
            }
        } else if (n % 2 != 0) {
            int midCol = n / 2;
            int sum = 0;
            for (int i = 0; i < m; i++) {
                sum += grid[i][midCol];
            }
            int colFlips = 0;
            for (int i = 0, j = m - 1; i < j; i++, j--) {
                if (grid[i][midCol] != grid[j][midCol]) {
                    colFlips++;
                }
            }
            flips += colFlips;
            if (sum % 4 != 0 && colFlips == 0) {
                flips += 2;
            }
        }
        return flips;
    }
}
