class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0,j=m-1;i<j;i++,j--){
            for(int k=0,l=n-1;k<l;k++,l--){
                int num=grid[i][k]+grid[i][l]+grid[j][k]+grid[j][l];
                ans+=min(num,4-num);
            }
        }
        int num1=0,num2=0,num3=0;
        if(m%2){
            int i=m/2;
            for(int j=0,k=n-1;j<k;j++,k--){
                if(grid[i][j]^grid[i][k]){
                    num1++;
                }
                else if(grid[i][j]==1){
                    num2++;
                }
                else{
                    num3++;
                }
            }
        }
        if(n%2){
            int i=n/2;
            for(int j=0,k=m-1;j<k;j++,k--){
                if(grid[j][i]^grid[k][i]){
                    num1++;
                }
                else if(grid[j][i]==1){
                    num2++;
                }
                else{
                    num3++;
                }
            }
        }
        if((m%2)&&(n%2)){
            ans+=grid[m/2][n/2];
        }
        if(num1==0&&num2%2==1){
            ans+=2;
        }
        ans+=num1;
        return ans;
    }
};
