class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])

        # get all 1
        res = 0
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 1:
                    res += 1

        for i in range(m // 2):
            for j in range(n // 2):
                cur = grid[i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][j] + grid[m - 1 - i][n - 1 - j]
                if cur == 4:
                    res -= 4
                if cur == 3:
                    res -= 2

        pair, single = 0, 0
        if n % 2 == 1:
            mid = n // 2
            for i in range(m // 2):
                if grid[i][mid] + grid[m - 1 - i][mid] == 2:
                    pair += 1
                if grid[i][mid] + grid[m - 1 - i][mid] == 1:
                    single += 1
        if m % 2 == 1:
            mid = m // 2
            for j in range(n // 2):
                if grid[mid][j] + grid[mid][n - 1 - j] == 2:
                    pair += 1
                if grid[mid][j] + grid[mid][n - 1 - j] == 1:
                    single += 1
        while pair >= 2:
            pair -= 2
            res -= 4
        if pair >= 1 and single >= 1:
            res -= 2
        return res
