#include <bits/stdc++.h>
using namespace std;

class Solution {
   public:
    int minFlips(vector<vector<int>>& grid) {
        auto& a = grid;
        int res = 0;
        int n = a.size(), m = a[0].size();
        int aa = 0, b = 0, c = 0;
        for (int i = 0; i <= n - 1 - i; ++i) {
            for (int j = 0; j <= m - 1 - j; ++j) {
                set<pair<int, int>> st;
                for (int x : {i, n - 1 - i}) {
                    for (int y : {j, m - 1 - j}) {
                        st.insert({x, y});
                    }
                }
                if (i == n - 1 - i || j == m - 1 - j) {
                    if (i == n - 1 - i && j == m - 1 - j) {
                        for (auto [x, y] : st) {
                            res += a[x][y];
                        }
                    } else {
                        int x, y;
                        if (i == n - 1 - i) {
                            x = a[i][j], y = a[i][m - 1 - j];
                        } else {
                            x = a[i][j], y = a[n - 1 - i][j];
                        }
                        if (x == 0 && y == 0) {
                            aa += 1;
                        }
                        if (x == 1 && y == 1) {
                            b += 1;
                        }
                        if (x != y) {
                            c += 1;
                        }
                    }
                } else {
                    int s = 0;
                    for (auto [x, y] : st) {
                        s += a[x][y];
                    }
                    res += min(s, 4 - s);
                }
            }
        }
        res += c;
        if (b % 2 != 0) {
            if (c == 0) {
                res += 2;
            }
        }
        return res;
    }
};
