class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])
        answer = 0
        for i in range(n):
            for j in range(m):
                if i < n-1-i and j < m-1-j:
                    a, b, c, d = grid[i][j], grid[i][m-1-j], grid[n-1-i][j], grid[n-1-i][m-1-j]
                    R = a+b+c+d
                    if R==1 or R==3:
                        answer+=1
                    elif R==2:
                        answer+=2
        zerozero = 0
        onezero = 0 
        oneone = 0
        if m % 2==1:
            for i in range(n):
                if i < n-1-i:
                    a, b = grid[i][(m-1)//2], grid[n-1-i][(m-1)//2]
                    if a==0 and b==0:
                        zerozero+=1
                    elif a==1 and b==1:
                        oneone+=1
                    else:
                        onezero+=1
        if n % 2==1:
            for j in range(m):
                if j < m-1-j:
                    a, b = grid[(n-1)//2][j], grid[(n-1)//2][m-1-j]
                    if a==0 and b==0:
                        zerozero+=1
                    elif a==1 and b==1:
                        oneone+=1
                    else:
                        onezero+=1
        if n % 2==1 and m % 2==1 and grid[(n-1)//2][(m-1)//2]==1:
            answer+=1
       # print(zerozero, onezero, oneone)
        answer+=onezero
        if (2*oneone) % 4==2 and onezero==0:
            answer+=2
        return answer
        #we now have 2*oneone+a ones
        #where 0 <= a <= onezero
