class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int n=arr.size();
        int m=arr[0].size();
        int nm=n/2;
        int mm=m/2;
        int res=0;
        for(int i=0;i<nm;i++){
            for(int j=0;j<mm;j++){
                int oc=0;
                if(arr[i][j]==1)oc++;
                if(arr[n-i-1][j]==1)oc++;
                if(arr[i][m-j-1]==1)oc++;
                if(arr[n-i-1][m-j-1]==1)oc++;

                res += min(oc,4-oc);
            }
        }
        int t_oc = 0;
        bool fl=0;
        if(n%2==1){
            for(int j=0;j<mm;j++){
                if(arr[nm][j]==arr[nm][m-j-1]){
                    if(arr[nm][j]==1){
                        t_oc+=2;
                    }
                }
                else{
                    res++;
                    fl=1;
                }
            }
        }

        if(m%2==1){
            for(int i=0;i<nm;i++){
                if(arr[i][mm]==arr[n-i-1][mm]){
                    if(arr[i][mm]==1){
                        t_oc+=2;
                    }
                }
                else{
                    res++;
                    fl=1;
                }
            }
        }
        if(t_oc%4!=0){
            if(!fl){
                res+=2;
            }
        }
        if(n%2==1&&m%2==1){
            if(arr[nm][mm]==1)res++;
        }

        return res;


    }
};
