class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int min = 0;
        for (int i=0; i<m/2; i++) {
            for (int j=0; j<n/2; j++) {
                int count = grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j];
                min += Math.min(count, 4-count);
            }
        }
        int one = 0;
        int mid = 0;
        if (m % 2 == 1) {
            int i = m/2;
            for (int j=0; j<n/2; j++) {
                int count = grid[i][j] + grid[i][n-1-j];
                if (count == 2) one ++;
                else if (count == 1) mid ++;
            }
        }
        if (n % 2 == 1) {
            int j = n/2;
            for (int i=0; i<m/2; i++) {
                int count = grid[i][j] + grid[m-1-i][j];
                if (count == 2) one ++;
                else if (count == 1) mid ++;
            }
        }
        if (m % 2 == 1 && n % 2 == 1) min += grid[m/2][n/2];
        if (one % 2 == 1 && mid == 0) mid = 2;
        return min + mid;
    }
}
