class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = m & 1 and n & 1 and grid[m>>1][n>>1]
        # print(ans)
        mark = False
        cnt = 0
        if m & 1:
            t = m >> 1
            for j in range(n>>1):
                if grid[t][j] != grid[t][n - 1 - j]:
                    mark = True
                    ans += 1
                else:
                    cnt += grid[t][j] == 1
        if n & 1:
            # print(True)
            t = n >> 1
            for i in range(m >> 1):
                if grid[i][t] != grid[m - 1 - i][t]:
                    mark = True
                    ans += 1
                else:
                    cnt += grid[i][t] == 1
        # print(ans)
        ans += 2 * ((mark == False) and (cnt & 1))
        for i in range(m >> 1):
            for j in range(n >> 1):
                c = 0
                c += (grid[i][j] == 1) + (grid[i][n - 1 - j] == 1) + (grid[m - 1- i][j] == 1) + (grid[m - 1 - i][n - 1 - j] == 1)
                if c == 4 or c == 0:
                    continue
                if c == 1 or c == 3:
                    ans += 1
                else:
                    ans += 2
        return ans
