class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0,temp,cnt=0;
        for(int i=0;i<grid.size()/2;i++)
        {
            for(int j=0;j<grid[0].size()/2;j++)
            {
                temp=0;
                if(grid[i][j])
                    temp++;
                if(grid[i][grid[0].size()-1-j])
                    temp++;
                if(grid[grid.size()-1-i][j])
                    temp++;
                if(grid[grid.size()-1-i][grid[0].size()-1-j])
                    temp++;
                ans+=min(temp,4-temp);
            }
        }
        temp=0;
        if(grid.size()%2)
        {
            for(int j=0;j<grid[0].size()/2;j++)
            {
                if(grid[grid.size()/2][j]!=grid[grid.size()/2][grid[0].size()-1-j])
                    temp++;
                else if(grid[grid.size()/2][j]==1)
                    cnt++;
            }
        }
        if(grid[0].size()%2)
        {
            for(int j=0;j<grid.size()/2;j++)
            {
                if(grid[j][grid[0].size()/2]!=grid[grid.size()-1-j][grid[0].size()/2])
                    temp++;
                else if(grid[j][grid[0].size()/2]==1)
                    cnt++;
            }
        }
        ans+=temp;
        if(temp==0&&cnt%2)
            ans+=2;
        if((grid.size()%2)&&(grid[0].size()%2))
        {
            if(grid[grid.size()/2][grid[0].size()/2])
                ans++;
        }
        return ans;
    }
};
