class Solution {
public:
    #define lol long long int

    long long int rec(int i,int rem,vector<pair<int,int>>&v,vector<vector<int>>&dp){
        if(i==v.size()){

            if(rem) return 1e9;
            else return 0;
        }
        if(dp[i][rem]!=-1) return dp[i][rem];

        lol tot = v[i].first+v[i].second;
        tot%=4;
        tot+=rem;
        tot%=4;
        lol one = v[i].first+rec(i+1,rem,v,dp);
        lol two = v[i].second + rec(i+1,tot,v,dp);

        return dp[i][rem]=min(one,two);
    }
    int minFlips(vector<vector<int>>& grid) {

        int m= grid.size(),n=grid[0].size();
        set<pair<int,int>>sp;
        vector<pair<int,int>>v;
        for(int i=0; i<m; i++){
            for(int j=0; j<n; j++){

                if(sp.find({i,j})==sp.end()){
                    set<pair<int,int>>pp;
                    pp.insert({i,j});pp.insert({m-1-i,j});pp.insert({i,n-1-j});pp.insert({m-1-i,n-1-j});
                    int tot = pp.size();
                    int cnt1=0,cnt2=0;
                    for(auto c:pp){
                        if(grid[c.first][c.second]==1) cnt1++;
                        if(grid[c.first][c.second]==0) cnt2++;
                    }
                    v.push_back({tot-cnt2,tot-cnt1});
                    sp.insert({i,j});sp.insert({m-1-i,j});sp.insert({i,n-1-j});sp.insert({m-1-i,n-1-j});

                }

            }
        }
        vector<vector<int>>dp(v.size()+1,vector<int>(4,-1));
        return rec(0,0,v,dp);

        for(auto c:v){
            cout<<c.first<<" "<<c.second<<endl;
        }
        return 0;
    }
};
