class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = 0;
        vector<int> cnta(5), cntb(3);
        for(int u = 0, d = n - 1; u <= d; ++u, --d) {
            for(int l = 0, r = m - 1; l <= r; ++l, --r) {
                if(u == d && l == r) {
                    int sum = grid[u][l];
                    if(sum == 0) continue;
                    ++ans;
                }
                else if(u == d || l == r) {
                    int sum = grid[u][l] + grid[d][r];
                    if(sum == 0) continue;
                    ++cntb[sum];
                }
                else {
                    int sum = grid[u][l] + grid[u][r] + grid[d][l] + grid[d][r];
                    if(sum == 0 || sum == 4) continue;
                    ++cnta[sum];
                }
            }
        }
        ans += cnta[1] + 2 * cnta[2] + cnta[3];
        if(cntb[1]) ans += cntb[1];
        else if(cntb[2] & 1) ans += 2;
        return ans;
    }
};
