class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int c=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                ans+=min(c,4-c);
            }
        }
        int extcol=0,extrow=0,countOne=0;
        if(m%2){
            for(int j=0;j<n/2;j++){
                if(grid[m/2][j]!=grid[m/2][n-1-j]) extrow++;
                else if(grid[m/2][j]) countOne++;
            }
        }
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]) extcol++;
                else if(grid[i][n/2]) countOne++;
            }
        }
        if(m%2 && n%2 && grid[m/2][n/2]) ans++;
        ans+=extcol+extrow;
        if(countOne%2 && extcol+extrow==0){
            ans+=2;
        }
        return ans;
    }
};
