class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int r=0;
        int a=0;
        int b=0;
        int c=0;
        for(int i=0;i<(grid.size()+1)/2;i++){
            for(int j=0;j<(grid[0].size()+1)/2;j++){
                if(i==grid.size()-1-i && j==grid[0].size()-1-j){
                    // cout<<i<<" "<<j<<endl;
                    r+=grid[i][j];continue;
                }
                if(i==grid.size()-1-i){
                    if(grid[i][j]==1 && grid[i][grid[0].size()-1-j]==1){
                        a++;
                    }
                    else if(grid[i][j]!=0 || grid[i][grid[0].size()-1-j]!=0){
                        b++;
                    }
                    else{
                        c++;
                    }
                    continue;
                }
                if(j==grid[0].size()-1-j){
                    if(grid[i][j]==1 && grid[grid.size()-1-i][j]==1){
                        a++;
                    }
                    else if(grid[i][j]!=0 || grid[grid.size()-1-i][j]!=0){
                        b++;
                    }
                    else{
                        c++;
                    }
                    continue;
                }
                int p=grid[i][j]+grid[grid.size()-1-i][j]+grid[i][grid[0].size()-1-j]+grid[grid.size()-1-i][grid[0].size()-1-j];
                r+=min(p,4-p);
            }
        }
        a%=2;
        if(a==0){
            r+=b;
        }
        else{
            if(b!=0){
                r+=b;
            }
            else {
                r+=2;
            }
        }
        return r;
    }
};
