class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        int ans = 0;
        int g=0,h=0;
        for(int i=0;i<((n+1)/2);++i){
            for(int j=0;j<((m+1)/2);++j){

                int p = 0,q=0;
                int x = n-1-i;
                int y = m-1-j;
                if(i==x&&y==j){
                    if(a[i][j]) ans++;
                }
                else if(i==x){
                    if(a[i][y]!=a[i][j]) ans++,h++;
                    else if(a[i][y]) g++;
                }else if(y==j){
                    if(a[i][y]!=a[x][j]) ans++,h++;  
                    else if(a[i][y]) g++; 
                }
                else{
                    if(a[i][j]) p++; else q++;
                    if(a[i][y]) p++; else q++;
                    if(a[x][j]) p++; else q++;
                    if(a[x][y]) p++; else q++;
                    ans+=min(p,q);
                }
            }
        }
        if(h==0 && (g&1)==1) ans+=2; 
        return ans;
    }

};
