class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 0;
        for (int i = 0; i < n/2; ++i) {
            for (int j = 0; j < m/2; ++j) {
                int ct = grid[i][j];
                ct+=grid[i][m-j-1];
                ct+=grid[n-i-1][j];
                ct+=grid[n-i-1][m-j-1];
                if (ct==2)
                    ans+=2;
                if (ct==1||ct==3)
                    ans++;
            }
        }
        int flips = 0;
        int ct = 0;
        if (n%2==1) {
            int i = n/2;
            for (int j = 0; j < m/2; ++j) {
                if (grid[i][j]!=grid[i][m-j-1])
                    ++flips;
                ct+=grid[i][j];
            }
        }
        if (m%2==1) {
            int j = m/2;
            for (int i = 0; i < n/2; ++i) {
                if (grid[i][j]!=grid[n-i-1][j])
                    ++flips;
                ct+=grid[i][j];
            }
        }
        //System.out.println(flips+" "+ct+" "+ans);
        if (flips==0&&ct%2==1)
            flips+=2;
        ans+=flips;
        if (n%2==1&&m%2==1)
            ans += grid[n/2][m/2];
        return ans;
    }
}
