class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        m, n = len(grid), len(grid[0])            
        res = 0

        for i in range(m//2):
            for j in range(n//2):
                v1 = grid[i][j]
                v2 = grid[i][n-1-j]
                v3 = grid[m-1-i][j]
                v4 = grid[m-1-i][n-1-j]

                c0 = v1+v2+v3+v4
                c1 = 4 - c0

                res += min(c0, c1)


        acc = 0
        vary = 0

        if m % 2 and n % 2:
            if grid[m//2][n//2] == 1:
                res += 1

            for i in range(m//2):
                if grid[i][n//2] == grid[m-1-i][n//2]:
                    if grid[i][n//2] == 1:
                        acc += 2
                else:
                    vary += 1

            for j in range(n//2):
                if grid[m//2][j] == grid[m//2][n-1-j]:
                    if grid[m//2][n-1-j] == 1:
                        acc += 2
                else:
                    vary += 1

        elif m % 2:

            for j in range(n//2):
                if grid[m//2][j] == grid[m//2][n-1-j]:
                    if grid[m//2][n-1-j] == 1:
                        acc += 2
                else:
                    vary += 1

        elif n % 2:

            for i in range(m//2):
                if grid[i][n//2] == grid[m-1-i][n//2]:
                    if grid[i][n//2] == 1:
                        acc += 2
                else:
                    vary += 1


        if acc % 4 == 2:
            if vary == 0:
                res += 2
            else:
                res += vary

        elif acc % 4 == 0:
            res += vary

        return res
