#include "bits/stdc++.h"
using namespace std;
#define ll long long



class Solution {
public:
	int minFlips(vector<vector<int>>& a) {
		int n = a.size(), m = a[0].size(), ans = 0;
		for(int i = 0; i < n / 2; i++) {
			for(int j = 0; j < m / 2; j++) {
				int f[2] = {};
				f[a[i][j]]++;
				f[a[n - 1 - i][j]]++;
				f[a[i][m - 1 - j]]++;
				f[a[n - 1 - i][m - 1 - j]]++;

				int mn = min(f[0], f[1]);
				ans += mn;
			}
		}

		int change = 0, one_pair = 0;
		if(n % 2 == 1) {
			for(int j = 0; j < m / 2; j++) {
				if(a[n / 2][j] == a[n / 2][m - 1 - j]) {
					if(a[n / 2][j] == 1) one_pair++;
				} else {
					change++;
				}
			}
		}
		if(m % 2 == 1) {
			for(int i = 0; i < n / 2; i++) {
				if(a[i][m / 2] == a[n - 1 - i][m / 2]) {
					if(a[i][m / 2] == 1) one_pair++;
				} else {
					change++;
				}
			}
		}
		if(one_pair % 2 == 1) {
			ans += change + 2 * (change == 0);
		} else {
			ans += change;
		}
		if(n % 2 == 1 && m % 2 == 1 && a[n / 2][m / 2] == 1) {
			ans++;
		}

		return ans;
	}
};

