class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0, f=0, tot=0, tmp;
        for (int i=0; i<(n+1)/2; i++){
            for (int j=0; j<(m+1)/2; j++){
                //cout<<i<<" "<<j<<'\n';
                if (i==n/2){
                    if (j==m/2){
                        ans+=grid[i][j];
                    }else{
                        if (grid[i][j]!=grid[i][m-1-j]){
                            f=1;
                            ans+=1;
                            // if I change, then make them both 0
                        }else{
                            tot+=(2*grid[i][j]);
                        }
                    }
                }else{
                    if (j==m/2){
                        if (grid[i][j]!=grid[n-1-i][j]){
                            f=1;
                            ans+=1;
                            // if I change, then make them both 0
                        }else{
                            tot+=(2*grid[i][j]);
                        }
                    }else{


                        tmp=grid[i][j];
                        tmp+=grid[n-1-i][j];
                        tmp+=grid[n-1-i][m-1-j];
                        tmp+=grid[i][m-1-j];

                        ans+=min(tmp, 4-tmp);
                    }

                }
            }
        }

        if (tot%4){
            if (!f) ans+=2;
        }
        return ans;
    }
};
