class Solution {
public:
    int minFlips(vector<vector<int>>& g) {

        int n=g.size(),m=g[0].size();

        vector<pair<int,int>> v;

        vector<vector<bool>> vis(n,vector<bool>(m,0));

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]) continue;
                set<pair<int,int>> ind;
                ind.insert({i,j});
                ind.insert({i,m-1-j});
                ind.insert({n-1-i,m-1-j});
                ind.insert({n-1-i,j});
                int cnt1=0,cnt0=0;
                for(auto p:ind){
                    if(g[p.first][p.second]) cnt1++;
                    else cnt0++;
                    vis[p.first][p.second]=1;
                }
                v.push_back({cnt1,cnt0});
            }
        }


        vector<vector<int>> dp(v.size(),vector<int>(4,-1));


        auto go=[&](auto&&go,int i,int rem)->int{
            if(i==v.size()){
                return (rem?1e9:0);
            }
            if(dp[i][rem]!=-1) return dp[i][rem];
            int ans=min(v[i].first+go(go,i+1,rem),v[i].second+go(go,i+1,(rem+v[i].first+v[i].second)%4));
            return dp[i][rem]=ans;
        };



        return go(go,0,0);






    }
};
