class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size();
        int ans=0;
        if((n&1) && (m&1) && (a[n/2][m/2]==1)) ans++;

        int ext=0,ct1=0;
        if(n&1){
            int ind=n/2;
            for(int j=0; j<m/2; j++) {
                if(a[ind][j]!=a[ind][m-1-j]) ext++;
                else if(a[ind][j]==1) ct1+=2;
            }
        }
        if(m&1){
            int ind=m/2;
            for(int i=0; i<n/2; i++) {
                if(a[i][ind]!=a[n-1-i][ind]) ext++;
                else if(a[i][ind]==1) ct1+=2;
            }
        }
        if(ct1%4==0) ans+=ext;
        else{
            if(ext) ans+=ext;
            else ans+=2;
        }

        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int ct=0;
                if(a[i][j]) ct++;
                if(a[n-i-1][j]) ct++;
                if(a[i][m-1-j]) ct++;
                if(a[n-i-1][m-j-1]) ct++;
                ans+=min(ct,4-ct);
            }
        }
        return ans;
    }
};
