class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        tot = 0
        for i in range(n // 2):
            for j in range(m // 2):
                x = grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j]
                tot += min(x, 4-x)
        bad, o = 0, 0
        if n % 2 == 1:
            for i in range(m // 2):
                a, b = grid[n // 2][i], grid[n // 2][m-1-i]
                if a != b:
                    bad += 1
                elif a == 1:
                    o += 2
        if m % 2 == 1:
            for i in range(n // 2):
                a, b = grid[i][m // 2], grid[n-i-1][m // 2]
                if a != b:
                    bad += 1
                elif a == 1:
                    o += 2
        if o % 4 != 0 and bad == 0:
            tot += 2
        else:
            tot += bad
        if m % 2 == 1 and n % 2 == 1 and grid[n // 2][m // 2] == 1:
            tot += 1
        return tot
