class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        bool lo = m % 2;
        bool ro = n % 2;
        int ans = 0, f = 0, cnt = 0;
        for (int i = 0; i < m/2; i++) 
            for (int j = 0; j < n/2; j++) {
                int s = grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j];
                ans += min(4 - s , s);
            }

        if (lo && ro) {
            ans += grid[m/2][n/2] == 1;
        }
        if (lo) {
            for (int i = 0, j = n - 1; i < j; ++i, --j) {
                if (grid[m/2][i] != grid[m/2][j]) {
                    f++;
                } else if (grid[m/2][i] == 1) {
                    cnt++;
                }
            }
        }
        if (ro) {
            for (int i = 0, j = m - 1; i < j; ++i, --j) {
                if (grid[i][n/2] != grid[j][n/2]) {
                    f++;
                } else if (grid[i][n/2] == 1) {
                    cnt++;
                }
            }
        }
        ans += f;
        if (cnt % 2 != 0 && f == 0) {
            ans += 2;
        }
        return ans;
    }
};
