class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
        int n = grid.size(), m = grid[0].size();

        int ans = 0;
        for(int i = 0; i < n / 2; ++i)
        {
            for(int j = 0; j < m / 2; ++j)
            {
                int cnt = grid[i][j] + grid[n - i - 1][j] + grid[i][m - j - 1] + grid[n - i - 1][m - j - 1];
                ans += min(cnt, 4 - cnt);
            }
        }

        if(n % 2 == 1 && m % 2 == 1)
        {
            ans += grid[n / 2][m / 2];
        }

        int ones = 0, op = 0;
        if(n % 2 == 1)
        {
            for(int j = 0; j < m / 2; ++j)
            {
                int k = m - j - 1;
                if(grid[n / 2][j] != grid[n / 2][k])
                {
                    ans++;
                    op++;
                }
                else if(grid[n / 2][j] == 1)
                {
                    ones += 2;
                }
            }
        }

        if(m % 2 == 1)
        {
            for(int i = 0; i < n / 2; ++i)
            {
                int k = n - i - 1;
                if(grid[i][m / 2] != grid[k][m / 2])
                {
                    ans++;
                    op++;
                }
                else if(grid[i][m / 2] == 1)
                {
                    ones += 2;
                }
            }
        }

        if(ones % 4 != 0)
        {
            if(op == 0)
            {
                ans += 2;
            }
        }

        return ans;
    }
};
