class Solution {
    public int minFlips(int[][] grid) {
        int n=grid.length;
        int m=grid[0].length;
        int count=0;
        int ans=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int ones=0;
                int zero=0;
                if(grid[i][j]==0)
                    zero++;
                else
                    ones++;
                if(grid[i][m-j-1]==0)
                    zero++;
                else
                    ones++;
                if(grid[n-i-1][j]==0)
                    zero++;
                else
                    ones++;
                if(grid[n-i-1][m-j-1]==0)
                    zero++;
                else
                    ones++;
                count=count+ones>=zero?4:0;
                ans+=Math.min(ones,zero);
            }
        }
        boolean check=false;
        if(n%2!=0)
        {
            for(int i=0;i<m/2;i++)
            {
                if(grid[n/2][i]!=grid[n/2][m-i-1])
                {
                    check=true;
                    ans++;
                    count+=2;
                }
                else if(grid[n/2][i]==1)
                    count+=2;
            }
        }
        if(m%2!=0)
        {
            for(int i=0;i<n/2;i++)
            {
                if(grid[i][m/2]!=grid[n-i-1][m/2])
                {
                    check=true;
                    ans++;
                    count++;
                }
                else if(grid[i][m/2]==1)
                    count+=2;
            }
        }
        if(n%2!=0 && m%2!=0)
            if(grid[n/2][m/2]==1)
                ans++;
        int rem = count%4;
        if(rem==2)
            if(!check)
                ans+=2;
        return ans;
    }
}
