class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        bool midrow=false;
        bool midcol=false;
        if(n%2==1){
            midrow=true;
        }
        if(m%2==1){
            midcol=true;
        }
        if(n==1&&m==1){
            return grid[0][0];
        }
        int ctr=0;
        //cout<<n<<" "<<m<<endl;
        for(int i=0;i<=n/2-1;i++){
            for(int j=0;j<=m/2-1;j++){
                int x=grid[i][j];
                int y=grid[i][m-1-j];
                int z=grid[n-1-i][j];
                int w=grid[n-1-i][m-1-j];

                int aa=x+y+z+w;
                //cout<<i<<"**"<<j<<" "<<aa<<endl;
                ctr+=min(aa,4-aa);
            }
        }
        //cout<<ctr<<endl;
        int tot=0;
        bool chang=false;
        if(midrow){
            for(int j=0;j<=m/2-1;j++){
                int x=grid[n/2][j];
                int y=grid[n/2][m-1-j];
                if(x!=y){
                    chang=true;
                    tot+=2;
                    ctr++;
                }
                else{
                    tot+=(x+y);
                }
            }
        }
        if(midcol){
            for(int i=0;i<=n/2-1;i++){
                int x=grid[i][m/2];
                int y=grid[n-1-i][m/2];
                if(x!=y){
                    chang=true;
                    tot+=2;
                    ctr++;
                }
                else{
                    tot+=(x+y);
                }
            }            
        }
        if(midrow&&midcol){
            ctr+=grid[n/2][m/2];
        }
        if(tot%4!=0){
            if(chang==true){
                //nothing
            }
            else{
                ctr+=2;
            }
        }
        return ctr;


    }
};
