class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        m, n = len(g), len(g[0])
        flips = 0
        for i in range(m//2):
            for j in range(n//2):
                tl = g[i][j]
                bl = g[m - i - 1][j]
                tr = g[i][n - j - 1]
                br = g[m - i - 1][n - j - 1]
                if (u:=sum([tl, bl, tr, br])) == 2:
                    flips += 2
                elif u == 1 or u == 3:
                    flips += 1
        if m & 1 and n & 1:
            if g[m//2][n//2]:
                flips += 1
        oc = 0
        fs = 0
        if m & 1:
            for j in range(n//2):
                if g[m//2][j] ^ g[m//2][n - j - 1]:
                    fs += 1
                    oc += 1
                elif g[m//2][j] & g[m//2][n - j - 1]:
                    oc += 1
        if n & 1:
            for i in range(m//2):
                if g[i][n//2] ^ g[m - i - 1][n//2]:
                    fs += 1
                    oc += 1
                elif g[i][n//2] & g[m - i - 1][n//2]:
                    oc += 1
        if oc & 1:
            if not fs:
                fs += 2
        return flips + fs
