class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n=len(grid)
        m=len(grid[0])
        res=0
        for i in range(n//2):
            for j in range(m//2):
                x=[grid[i][j],grid[n-1-i][j],grid[i][m-1-j],grid[n-1-i][m-1-j]]
                ones=x.count(1)
                if ones==0 or ones==4:
                    continue
                elif ones<=2:
                    res+=ones
                else:
                    res+=4-ones
        if n%2 and m%2:
            res+=grid[n//2][m//2]
        ones1 = 0
        changes1 = 0 
        if n%2:
            for i in range(m//2):
                if grid[n//2][i]!=grid[n//2][m-1-i]:
                    changes1+=1
                elif grid[n//2][i]==1:
                    ones1+=1
        ones2 = 0
        changes2 = 0 
        if m%2:
            for i in range(n//2):
                if grid[i][m//2]!=grid[n-1-i][m//2]:
                    changes2+=1
                elif grid[i][m//2]==1:
                    ones2+=1
        totalones=ones1+ones2
        totalones%=2
        totalchanges=changes1+changes2
        if totalchanges>0:
            res+=totalchanges
        elif totalones:
            res+=2
        return res
