class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = 0

        m = len(grid)
        n = len(grid[0])
        f = 0
        g = 0
        for i in range((m+1) // 2):
            for j in range((n+1) // 2):
                p = 0
                if not (m % 2 == 1 and 2 * i + 1 == m):
                    p += 1
                if not (n % 2 == 1 and 2 * j + 1 == n):
                    p += 1

                if p == 0:
                    if grid[i][j] == 1:
                        ans += 1
                elif p == 2:
                    tmp = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 -j] + grid[m - 1 - i][n - 1 - j]
                    ans += min(tmp, 4 - tmp)
                elif p == 1:
                    tmp = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 -j] + grid[m - 1 - i][n - 1 - j]
                    tmp = tmp // 2
                    ans += min(tmp, 2 - tmp)
                    if tmp == 1:
                        f += 1
                    elif tmp == 2:
                        g += 1
        if g % 2 == 1 and f == 0:
            ans += 2

        return ans
