class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int one = 0, zer = 0;
                grid[i][j]?one++:zer++;
                grid[i][m-j-1]?one++:zer++;
                grid[n-i-1][j]?one++:zer++;
                grid[n-i-1][m-j-1]?one++:zer++;
                ans+=min(zer,one);
            }
        }
        int one = 0, zer = 0, both = 0;
        if (n%2 == 1){
            for(int i=0;i<m/2;i++){
                if (grid[n/2][i]==1 && grid[n/2][m-i-1]==1)one++;
                if (grid[n/2][i]==0 && grid[n/2][m-i-1]==0)zer++;
                if (grid[n/2][i]!=grid[n/2][m-i-1])both++;
            }
        }
        if (m%2 == 1){
            for(int i=0;i<n/2;i++){
                if (grid[i][m/2]==1 && grid[n-i-1][m/2]==1)one++;
                if (grid[i][m/2]==0 && grid[n-i-1][m/2]==0)zer++;
                if (grid[i][m/2]!=grid[n-i-1][m/2])both++;
            }
        }
        int single = 0;
        if (n%2 && m%2){
            single = grid[n/2][m/2];
        }
        ans+=single;
        ans+=both;
        //cout<<one<<" "<<zer<<" "<<both<<endl;
        if ((one)%2 == 1){
            if (both==0)ans+=2;
        }

        return ans;
    }
};
