class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = p11 = p01 = 0
        m, n = len(grid), len(grid[0])

        for i in range((m + 1) >> 1):
            for j in range((n + 1) >> 1):
                s = 1
                cnt = grid[i][j]

                i0 = m - 1 - i
                j0 = n - 1 - j

                if i0 != i:
                    s += 1
                    cnt += grid[i0][j]
                if j0 != j:
                    s += 1
                    cnt += grid[i][j0]
                if i0 != i and j0 != j:
                    s += 1
                    cnt += grid[i0][j0]

                if s == 4:
                    ans += min(cnt, s - cnt)
                elif s == 1:
                    ans += cnt
                elif cnt == 1:
                    p01 += 1
                elif cnt == 2:
                    p11 += 1

        r = p11 * 2 & 3
        if p01 == 0:
            return ans + r

        return ans + p01
