class Solution
{
    public int minFlips(int[][] grid)
    {
        int n = grid.length, m = grid[0].length;
        boolean[][] visited = new boolean[n][m];
        int minNum = 0, num1 = 0;
        boolean bool = false;
        for (int r=0;r<n;r++)
        {
            for (int c=0;c<m;c++)
            {
                int c0 = 0, c1 = 0;
                if (!visited[r][c])
                {
                    visited[r][c] = true;
                    if (grid[r][c] == 0)
                    {
                        c0++;
                    }
                    else
                    {
                        c1++;
                    }
                }
                if (!visited[n - r - 1][c])
                {
                    visited[n - r - 1][c] = true;
                    if (grid[n - r - 1][c] == 0)
                    {
                        c0++;
                    }
                    else
                    {
                        c1++;
                    }
                }
                if (!visited[r][m - c - 1])
                {
                    visited[r][m - c - 1] = true;
                    if (grid[r][m - c - 1] == 0)
                    {
                        c0++;
                    }
                    else
                    {
                        c1++;
                    }
                }
                if (!visited[n - r - 1][m - c - 1])
                {
                    visited[n - r - 1][m - c - 1] = true;
                    if (grid[n - r - 1][m - c - 1] == 0)
                    {
                        c0++;
                    }
                    else
                    {
                        c1++;
                    }
                }
                int sum = c0 + c1;
                if (sum == 0)
                {
                    continue;
                }
                if (sum == 1)
                {
                    if (c1 == 1)
                    {
                        minNum++;
                    }
                }
                if (sum == 2)
                {
                    if (c1 == 1)
                    {
                        minNum++;
                        num1 += 2;
                        bool = true;
                    }
                    if (c1 == 2)
                    {
                        num1 += 2;
                    }
                }
                if (sum == 4)
                {
                    minNum += Math.min(c0, c1);
                }
            }
        }
        if (num1 % 4 == 0 || bool)
        {
            return minNum;
        }
        minNum += 2;
        return minNum;
    }
}
