class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        res=0
        for i in range(m//2):
            for j in range(n//2):
                s=grid[i][j]+grid[i][~j]+grid[~i][j]+grid[~i][~j]
                res+=min(s,4-s)
        if m%2 and n%2:
            res+=grid[m//2][n//2]
        d=s=0
        if m%2:
            for j in range(n//2):
                s+=(grid[m//2][j]+grid[m//2][~j])//2*2
                d+=grid[m//2][j]!=grid[m//2][~j]
        if n%2:
            for i in range(m//2):
                s+=(grid[i][n//2]+grid[~i][n//2])//2*2
                d+=grid[i][n//2]!=grid[~i][n//2]
        if s%4 and d==0:
            d+=2
        return res+d
