class Solution {
 public:
  int minFlips(vector<vector<int>>& grid) {
    int n = grid.size();
    int m = grid[0].size();
    int ret = 0;
    int cnt01 = 0;
    int cnt00 = 0;
    int cnt11 = 0;
    for (int i = 0; i < n; ++i) {
      for (int j = 0; j < m; ++j) {
        int ri = n - 1 - i;
        int rj = m - 1 - j;
        if (ri < i || rj < j) {
          continue;
        }
        if (ri == i && rj == j) {
          if (grid[i][j] == 1) {
            ++ret;
          }
          continue;
        }
        if (ri == i) {
          if (grid[i][j] == grid[i][rj]) {
            if (grid[i][j] == 1) {
              cnt11++;
            } else {
              cnt00++;
            }
          } else {
            cnt01++;
          }
          continue;
        }
        if (rj == j) {
          if (grid[i][j] == grid[ri][j]) {
            if (grid[i][j] == 1) {
              cnt11++;
            } else {
              cnt00++;
            }
          } else {
            cnt01++;
          }
          continue;
        }
        int cnt0 = 0;
        if (grid[i][j] == 0) {
          ++cnt0;
        }
        if (grid[i][rj] == 0) {
          ++cnt0;
        }
        if (grid[ri][j] == 0) {
          ++cnt0;
        }
        if (grid[ri][rj] == 0) {
          ++cnt0;
        }
        if (cnt0 == 4 || cnt0 == 0) {
          ret += 0;
        } else if (cnt0 == 3 || cnt0 == 1) {
          ret += 1;
        } else {
          ret += 2;
        }
      }
    }
    if (cnt01 >= 1) {
      ret += cnt01;
      return ret;
    }
    if (cnt11 % 2 == 0) {
      ret += cnt01;
      return ret;
    }
    ret += 2;
    return ret;
  }
};
