class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        ans = 0
        m,n = len(g), len(g[0])
        t1 = t2 = 0
        if m%2 == 1 and n%2 == 1:
            if g[m//2][n//2] == 1:
                ans += 1
        for i in range(m//2):
            for j in range(n//2):
                tt = (g[i][j] == 1) + (g[m-1-i][j] == 1) + (g[i][n-1-j] == 1) + (g[m-1-i][n-1-j] == 1)
                ans += min(tt,4-tt)
        ttt = 0
        no_oper = 0
        if m%2 == 1:
            for i in range(n//2):
                ttt += (g[m//2][i] == 1) + (g[m//2][n-1-i] == 1)
                if g[m//2][i] != g[m//2][n-1-i]:
                    no_oper += 1
        if n%2 == 1:
            for i in range(m//2):
                ttt += (g[i][n//2] == 1) + (g[m-1-i][n//2] == 1)
                if g[i][n//2] != g[m-1-i][n//2]:
                    no_oper += 1
        oper = 0
        if ttt % 4 != 0:
            oper = min(ttt % 4,abs(4 - (ttt % 4)))
        if(no_oper >= oper):
            ans += no_oper
        else:
            ans += no_oper + 2
        return ans
