class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int cnt=0,n=grid.size(), m=grid[0].size();
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int temp=0;
                temp+=grid[i][j];
                temp+=grid[n-i-1][m-j-1];
                temp+=grid[i][m-j-1];
                temp+=grid[n-i-1][j];
                if(temp==0 || temp==4)
                continue;
                else if(temp==1 || temp==3)
                cnt+=1;
                else
                cnt+=2;
            }
        }
        int ones=0,halves=0,zeroes=0;
        if(n&1)
        {
            for(int i=0;i<m/2;i++)
            {
                if(i==m-i-1)
                continue;
                int c=grid[n/2][i]+grid[n/2][m-i-1];
                if(c==1)
                {
                    cnt++;
                    halves++;
                }
                else if(c==0)
                {
                    zeroes++;
                }
                else
                {
                    ones++;
                }
            }
        }
        if(m&1)
        {
            for(int i=0;i<n/2;i++)
            {
                if(i==n-i-1)
                continue;
                int c=grid[i][m/2]+grid[n-i-1][m/2];
                if(c==1)
                {
                    cnt++;
                    halves++;
                }
                else if(c==0)
                {
                    zeroes++;
                }
                else
                {
                    ones++;
                }
            }
        }
        if((n&1) && (m&1))
        {
            if(grid[n/2][m/2])
            cnt++;
        }
        ones%=2;
        if(ones)
        {
            if(halves==0)
            cnt+=2;
        }
        return cnt;
    }
};
