using ll = long long;
using vi = vector<ll>;
using vvi = vector<vi>;
using pii = pair<ll,ll>;
using vp = vector<pii>;
using ti3 = tuple<ll,ll,ll>;
using vti3 = vector<ti3>;
using vs = vector<string>;

template<typename T>
using min_pq = priority_queue<T, vector<T>, greater<T>>;
template<typename T>
using max_pq = priority_queue<T>;

class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        ll n = a.size(), m = a[0].size();

        vi c(5);
        for (int i=0; i<n/2; i++) {
            for (int j=0; j<m/2; j++) {
                vi ii = {i, n-1-i};
                vi jj = {j, m-1-j};
                ll cnt = 0;
                for (auto x : ii) {
                    for (auto y : jj) {
                        if (a[x][y]) cnt++;
                    }
                }
                c[cnt]++;
            }
        }
        ll ans = 0;
        ans += c[1] + c[3] + 2*c[2];
        bool flag = false;
        ll cnt = 0;
        if (n&1) {
            for (int j=0; j<m/2; j++) {
                if (a[n/2][j] != a[n/2][m-1-j]) {
                    flag = true;
                    ans++;
                }
                else {
                    if (a[n/2][j] == 1) cnt += 2;
                }
            }
        }
        if (m&1) {
            for (int i=0; i<n/2; i++) {
                if (a[i][m/2] != a[n-1-i][m/2]) {
                    flag = true;
                    ans++;
                }
                else {
                    if (a[i][m/2] == 1) cnt += 2;
                }
            }
        }
        if (!flag && (cnt % 4 == 2)) ans += 2;
        if ((n&1) && (m&1)) {
            if (a[n/2][m/2]) ans++;
        }
        return ans;
    }
};
