class Solution:
    def minFlips(self, a: List[List[int]]) -> int:
        n = len(a)
        m = len(a[0])
        n2 = n // 2
        m2 = m // 2
        if n % 2 == 1:
            if m % 2 == 1:
                ans = a[n2][m2]
                d = 0
                res = 0
                for i in range(n2):
                    if a[i][m2] != a[n - 1 - i][m2]:
                        d += 1
                    elif a[i][m2] == 1:
                        res += 1
                for j in range(m2):
                    if a[n2][j] != a[n2][m - 1 - j]:
                        d += 1
                    elif a[n2][j] == 1:
                        res += 1
                if res % 2 == 1:
                    if d > 0:
                        ans += d
                    else:
                        ans += 2
                else:
                    ans += d

            else:
                ans = 0
                d = 0
                res = 0
                for j in range(m2):
                    if a[n2][j] != a[n2][m - 1 - j]:
                        d += 1
                    elif a[n2][j] == 1:
                        res += 1
                if res % 2 == 1:
                    if d > 0:
                        ans += d
                    else:
                        ans += 2
                else:
                    ans += d
        else:
            if m % 2 == 1:
                ans = 0
                d = 0
                res = 0
                for i in range(n2):
                    if a[i][m2] != a[n - 1 - i][m2]:
                        d += 1
                    elif a[i][m2] == 1:
                        res += 1
                if res % 2 == 1:
                    if d > 0:
                        ans += d
                    else:
                        ans += 2
                else:
                    ans += d

            else:
                ans = 0

        l = [0,1,2,1,0]
        for i in range(n2):
            for j in range(m2):
                res = [a[i][j], a[i][m-1-j], a[n-1-i][j], a[n-1-i][m-1-j]]
                s = sum(res)
                ans += l[s]
        return ans
