class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        const int inf=1e9;
        int n=size(grid),m=size(grid[0]);
        int ans=0;
        vector<int>twos;
        for(int i=0;i*2<=n-1;i++)
        {
            for(int j=0;j*2<=m-1;j++)
            {
                set<pair<int,int>>idx;
                idx.emplace(i,j);
                idx.emplace(n-1-i,j);
                idx.emplace(i,m-1-j);
                idx.emplace(n-1-i,m-1-j);
                int cnt=0;
                vector<int>vv(2);
                for(auto[a,b]:idx)
                {
                    vv[grid[a][b]]++;
                }
                //ans+=(int)size(idx)-max(vv[0],vv[1]);
                if(size(idx)==1)ans+=vv[1];
                if(size(idx)==2)twos.push_back(vv[1]);
                if(size(idx)==4)ans+=(int)size(idx)-max(vv[0],vv[1]);
            }
        }
        array<int,2>dp{0,inf};
        for(int i:twos)
        {
            array<int,2>dp1{inf,inf};
            dp1[1]=min(dp1[1],dp[0]+2-i);
            dp1[0]=min(dp1[0],dp[1]+2-i);
            dp1[1]=min(dp1[1],dp[1]+i);
            dp1[0]=min(dp1[0],dp[0]+i);
            dp=dp1;
        }
        return ans+dp[0];
    }
};
