class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int cnt=0,sum=0;
        for(int i=0;i<(n/2);i++){
            for(int j=0;j<(m/2);j++){
                sum=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                if(sum==1 || sum==3) cnt+=1;
                else if(sum==2) cnt+=2;
            }
        }
        int cnt1=0,one=0;
        if(n%2==1){
            int i=(n/2);
            for(int j=0;j<(m/2);j++){
                if(grid[i][j]!=grid[i][m-1-j]) cnt1++;
                else if(grid[i][j]==1) one+=2;
            }
        }
        if(m%2==1){
            int j=(m/2);
            for(int i=0;i<(n/2);i++){
                if(grid[i][j]!=grid[n-1-i][j]) cnt1++;
                else if(grid[i][j]==1) one+=2;
            }
        }
        if(one%4!=0 && cnt1==0){
            cnt1=2;
        }
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1) cnt++;
        return cnt+cnt1;;
    }
};
